export default function GradientTabs({ tabs, className }: GradientTabsProps) {
	const [activeTab, setActiveTab] = useState(tabs[0].id);

	return (
		<div className={`w-full max-w-4xl mx-auto ${className || ""}`}>
			{/* Tab Navigation */}
