										{[...Array(3)].map((_, i) => (
											<motion.div
												key={i}
												className={`h-2 w-2 rounded-full ${tab.gradient.replace("bg-gradient-to-r", "bg-gradient-to-br")}`}
												initial={{ scale: 0 }}
