"use client";
import type React from "react";
import { useEffect, useState } from "react";

const StackerLogo = ({ className }: { className?: string }) => (
	<svg
		className={className}
		viewBox="0 0 32 32"
		fill="none"
		xmlns="http://www.w3.org/2000/svg"
	>
		<path
			d="M16 0C7.163 0 0 7.163 0 16s7.163 16 16 16 16-7.163 16-16S24.837 0 16 0z"
			fill="#4F46E5"
		/>
		<path d="M19.333 9.333h-6.666v1.334h6.666V9.333z" fill="#fff" />
		<path d="M22.667 15.333h-10v1.334h10v-1.334z" fill="#fff" />
		<path d="M16 21.333h-3.333v1.334H16v-1.334z" fill="#fff" />
	</svg>
);

const PlayIcon = ({ className }: { className?: string }) => (
	<svg
		className={className}
		viewBox="0 0 24 24"
		fill="currentColor"
		xmlns="http://www.w3.org/2000/svg"
	>
		<path d="M8 5v14l11-7z" />
	</svg>
);

const MenuIcon = ({ className }: { className?: string }) => (
	<svg
		className={className}
		stroke="currentColor"
		fill="none"
		strokeWidth="2"
		viewBox="0 0 24 24"
		strokeLinecap="round"
		strokeLinejoin="round"
		xmlns="http://www.w3.org/2000/svg"
	>
		<line x1="3" y1="12" x2="21" y2="12"></line>
		<line x1="3" y1="6" x2="21" y2="6"></line>
		<line x1="3" y1="18" x2="21" y2="18"></line>
	</svg>
);

const CloseIcon = ({ className }: { className?: string }) => (
	<svg
		className={className}
		stroke="currentColor"
		fill="none"
		strokeWidth="2"
		viewBox="0 0 24 24"
		strokeLinecap="round"
		strokeLinejoin="round"
		xmlns="http://www.w3.org/2000/svg"
	>
		<line x1="18" y1="6" x2="6" y2="18"></line>
		<line x1="6" y1="6" x2="18" y2="18"></line>
	</svg>
);

const HeroSection: React.FC = () => {
	const [isMenuOpen, setIsMenuOpen] = useState(false);

	const navLinks = ["Home", "Solutions", "Product", "Resources", "Pricing"];

	useEffect(() => {
		if (isMenuOpen) {
			document.body.style.overflow = "hidden";
		} else {
			document.body.style.overflow = "unset";
		}
		return () => {
			document.body.style.overflow = "unset";
		};
	}, [isMenuOpen]);

	return (
		<div className="bg-gray-50 dark:bg-black font-sans text-gray-800 dark:text-gray-200 w-full">
			<div className="w-full">
				<header className="py-6 px-4 sm:px-6 lg:px-8">
					<nav className="flex items-center justify-between mx-auto">
						<div className="flex items-center gap-2">
							<StackerLogo className="h-8 w-8" />
							<span className="font-bold text-2xl text-gray-900 dark:text-white">
								stacker
							</span>
						</div>

						<div className="hidden lg:flex items-center gap-8">
							{navLinks.map((link) => (
								<a
									key={link}
									href="#"
									className="text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white transition-colors"
								>
									{link}
								</a>
							))}
						</div>

						<a
							href="#"
							className="hidden lg:inline-block bg-indigo-600 dark:bg-indigo-500 text-white font-semibold px-5 py-2 rounded-lg hover:bg-indigo-700 dark:hover:bg-indigo-600 transition-colors shadow-sm"
						>
							Get Started
						</a>

						<div className="lg:hidden">
							<button
								onClick={() => setIsMenuOpen(true)}
								className="text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white"
							>
								<MenuIcon className="h-6 w-6" />
							</button>
						</div>
					</nav>
				</header>

				<div
					className={`lg:hidden fixed inset-0 z-40 transition-opacity duration-300 ${isMenuOpen ? "opacity-100" : "opacity-0 pointer-events-none"}`}
				>
					<div
						className="absolute inset-0 bg-black/60 dark:bg-black/80"
						onClick={() => setIsMenuOpen(false)}
					></div>

					<div
						className={`relative z-50 bg-white dark:bg-gray-900 h-full w-4/5 max-w-sm ml-auto p-6 flex flex-col transition-transform duration-300 ease-in-out ${isMenuOpen ? "translate-x-0" : "translate-x-full"}`}
					>
						<div className="flex items-center justify-between mb-8">
							<span className="font-bold text-2xl text-gray-900 dark:text-white">
								Menu
							</span>
							<button
								onClick={() => setIsMenuOpen(false)}
								className="text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white"
							>
								<CloseIcon className="h-6 w-6" />
							</button>
						</div>
						<nav className="flex flex-col items-start gap-5">
							{navLinks.map((link) => (
								<a
									key={link}
									href="#"
									className="text-gray-800 dark:text-gray-200 hover:text-indigo-600 dark:hover:text-indigo-400 transition-colors text-lg w-full text-left py-2"
								>
									{link}
								</a>
							))}
							<a
								href="#"
								className="w-full mt-6 text-center bg-indigo-600 dark:bg-indigo-500 text-white font-semibold px-5 py-3 rounded-lg hover:bg-indigo-700 dark:hover:bg-indigo-600 transition-colors shadow-sm"
							>
								Get Started
							</a>
						</nav>
					</div>
				</div>

				<main className="relative flex-1 flex items-center justify-center text-center w-full">
					<div className="relative flex flex-col items-center justify-center py-10 sm:py-16 px-4 max-w-5xl mx-auto">
						<h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold text-gray-900 dark:text-white leading-tight max-w-4xl">
							Where your{" "}
							<span className="text-indigo-600 dark:text-indigo-400">data</span>{" "}
							turns into software with a click
						</h1>
						<p className="mt-6 text-lg sm:text-xl text-gray-600 dark:text-gray-300 max-w-2xl">
							Build custom portals, CRMs, and tools effortlessly. From concept
							to launch in minutes, not months.
						</p>
						<button className="mt-8 flex items-center gap-3 bg-gray-900 dark:bg-white text-white dark:text-gray-900 font-semibold px-6 py-3 rounded-lg hover:bg-gray-800 dark:hover:bg-gray-100 transition-all shadow-md transform hover:scale-105">
							<PlayIcon className="h-6 w-6" />
							<span>Watch Demo</span>
						</button>
					</div>
				</main>
			</div>
		</div>
	);
};

export default HeroSection;

"use client";
import type React from "react";
import { useState } from "react";

interface NavLinkProps {
	href: string;
	children: React.ReactNode;
	isActive?: boolean;
}

interface ButtonProps {
	children: React.ReactNode;
	variant?: "primary" | "secondary" | "outline";
	className?: string;
}

interface MobileMenuProps {
	isOpen: boolean;
	navItems: string[];
}

const LogoIcon: React.FC = () => (
	<svg
		width="24"
		height="24"
		viewBox="0 0 24 24"
		fill="none"
		xmlns="http://www.w3.org/2000/svg"
	>
		<path d="M6 6H10V10H6V6Z" fill="#F97316" />
		<path d="M14 6H18V10H14V6Z" fill="#F97316" />
		<path d="M6 14H10V18H6V14Z" fill="#F97316" />
		<path d="M14 14H18V18H14V14Z" fill="#F97316" fillOpacity="0.5" />
	</svg>
);

const MenuIcon: React.FC = () => (
	<svg
		className="w-6 h-6"
		fill="none"
		stroke="currentColor"
		viewBox="0 0 24 24"
		xmlns="http://www.w3.org/2000/svg"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M4 6h16M4 12h16m-7 6h7"
		/>
	</svg>
);

const CloseIcon: React.FC = () => (
	<svg
		className="w-6 h-6"
		fill="none"
		stroke="currentColor"
		viewBox="0 0 24 24"
		xmlns="http://www.w3.org/2000/svg"
	>
		<path
			strokeLinecap="round"
			strokeLinejoin="round"
			strokeWidth={2}
			d="M6 18L18 6M6 6l12 12"
		/>
	</svg>
);

const NavLink: React.FC<NavLinkProps> = ({
	href,
	children,
	isActive = false,
}) => (
	<a
		href={href}
		className={`px-3 py-2 rounded-md text-sm font-medium transition-colors duration-300 ${
			isActive
				? "text-orange-500"
				: "text-gray-600 dark:text-gray-300 hover:text-orange-500"
		}`}
	>
		{children}
	</a>
);

const Button: React.FC<ButtonProps> = ({
	children,
	variant = "primary",
	className = "",
}) => {
	const baseClasses =
		"px-5 py-2.5 rounded-lg font-semibold text-sm transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-offset-2 transform hover:scale-105";

	const variants = {
		primary:
			"bg-gray-900 dark:bg-white text-white dark:text-black hover:bg-gray-800 dark:hover:bg-gray-200 focus:ring-gray-900 dark:focus:ring-gray-300",
		secondary:
			"bg-white dark:bg-gray-900 text-gray-900 dark:text-white hover:bg-gray-200 dark:hover:bg-gray-800 focus:ring-gray-300 dark:focus:ring-gray-600 shadow-sm border border-gray-200 dark:border-gray-700",
		outline:
			"bg-white dark:bg-black text-gray-900 dark:text-white border border-gray-300 dark:border-gray-600 hover:bg-gray-100 dark:hover:bg-gray-900 focus:ring-gray-300 dark:focus:ring-gray-600",
	};

	return (
		<button className={`${baseClasses} ${variants[variant]} ${className}`}>
			{children}
		</button>
	);
};

const MobileMenu: React.FC<MobileMenuProps> = ({ isOpen, navItems }) => (
	<div
		className={`
      md:hidden absolute top-full left-0 w-full bg-white/95 dark:bg-black/95 backdrop-blur-sm border-t border-gray-200 dark:border-gray-700 shadow-lg
      transition-all duration-300 ease-in-out
      ${isOpen ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4 pointer-events-none"}
  `}
	>
		<div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
			{navItems.map((item) => (
				<a
					key={item}
					href={`#${item.toLowerCase()}`}
					className="text-gray-700 dark:text-gray-300 hover:text-orange-500 hover:bg-gray-50 dark:hover:bg-gray-800 block px-3 py-2 rounded-md text-base font-medium"
				>
					{item}
				</a>
			))}
		</div>
		<div className="pt-4 pb-4 border-t border-gray-200 dark:border-gray-700">
			<div className="px-5">
				<Button variant="outline" className="w-full">
					Buy Template
				</Button>
			</div>
		</div>
	</div>
);

const HeaderSection: React.FC = () => {
	const [isMenuOpen, setIsMenuOpen] = useState(false);
	const [activeLink] = useState("Features");
	const navItems = ["About", "Features", "Blog", "Pricing", "Contact"];

	return (
		<header className="relative z-20">
			<div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
				<div className="flex items-center justify-between h-20">
					<div className="flex-shrink-0 flex items-center gap-2">
						<LogoIcon />
						<span className="text-xl font-bold text-gray-900 dark:text-white">
							Qupe
						</span>
					</div>
					<nav className="hidden md:flex items-center space-x-1 bg-gray-100/50 dark:bg-gray-800/50 p-1 rounded-full">
						{navItems.map((item) => (
							<NavLink
								key={item}
								href={`#${item.toLowerCase()}`}
								isActive={activeLink === item}
							>
								{item}
							</NavLink>
						))}
					</nav>
					<div className="hidden md:block">
						<Button variant="outline">Buy Template</Button>
					</div>
					<div className="md:hidden">
						<button
							onClick={() => setIsMenuOpen(!isMenuOpen)}
							className="inline-flex items-center justify-center p-2 rounded-md text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white hover:bg-gray-100 dark:hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-orange-500"
							aria-expanded={isMenuOpen}
							aria-controls="mobile-menu"
						>
							<span className="sr-only">Open main menu</span>
							{isMenuOpen ? <CloseIcon /> : <MenuIcon />}
						</button>
					</div>
				</div>
			</div>
			<MobileMenu isOpen={isMenuOpen} navItems={navItems} />
		</header>
	);
};

const Hero: React.FC = () => (
	<section className="relative z-10 text-center py-16 sm:py-24 px-4">
		<div className="max-w-4xl mx-auto">
			<span className="inline-block px-4 py-1.5 text-xs font-semibold tracking-wider text-orange-600 dark:text-orange-400 uppercase bg-orange-100 dark:bg-orange-900/30 rounded-full">
				Qupe Finance
			</span>
			<h1 className="mt-6 text-4xl sm:text-5xl lg:text-7xl font-bold tracking-tight text-gray-900 dark:text-white leading-tight">
				You&apos;ve never made a website this{" "}
				<span className="text-orange-500">fast before</span>
			</h1>
			<p className="mt-6 max-w-2xl mx-auto text-lg text-gray-600 dark:text-gray-300">
				Gain financial acumen using our expert tools and insights to efficiently
				manage your money and enhance personal wealth.
			</p>
			<div className="mt-10 flex flex-col sm:flex-row items-center justify-center gap-4">
				<Button variant="primary" className="w-full sm:w-auto">
					Get started - for free
				</Button>
				<Button variant="secondary" className="w-full sm:w-auto">
					Discover Qupe
				</Button>
			</div>
		</div>
	</section>
);

export default function Hero2() {
	return (
		<div className="relative w-full overflow-hidden bg-white dark:bg-black">
			<div className="absolute top-0 left-0 -translate-x-1/2 -translate-y-1/2 pointer-events-none">
				<div className="w-[40rem] h-[40rem] bg-gradient-to-tr from-orange-200 dark:from-orange-800/30 to-transparent opacity-20 dark:opacity-10 rounded-full blur-3xl" />
			</div>
			<div className="absolute bottom-0 right-0 translate-x-1/2 translate-y-1/2 pointer-events-none">
				<div className="w-[40rem] h-[40rem] bg-gradient-to-bl from-orange-200 dark:from-orange-800/30 to-transparent opacity-20 dark:opacity-10 rounded-full blur-3xl" />
			</div>
			<HeaderSection />
			<main>
				<Hero />
			</main>
		</div>
	);
}

"use client";
import { ArrowRight, ChevronDown, Menu, X } from "lucide-react";
import type React from "react";
import { useEffect, useRef, useState } from "react";

const SolancnUIHero = () => {
	const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
	const [openDropdown, setOpenDropdown] = useState<string | null>(null);
	const navRef = useRef<HTMLElement>(null);

	const Dropdown = ({
		title,
		children,
		isOpen,
		onToggle,
	}: {
		title: string;
		children: React.ReactNode;
		isOpen: boolean;
		onToggle: () => void;
	}) => {
		return (
			<div className="relative">
				<div onClick={onToggle} className="cursor-pointer">
					<div className="flex items-center gap-1 text-sm font-medium text-slate-600 hover:text-slate-900 dark:text-slate-300 dark:hover:text-slate-100 transition-colors">
						<span>{title}</span>
						<ChevronDown
							className={`w-4 h-4 transition-transform duration-200 ${isOpen ? "rotate-180" : ""}`}
						/>
					</div>
				</div>
				{isOpen && (
					<div className="absolute top-full left-1/2 -translate-x-1/2 mt-2 w-56 origin-top-right rounded-md bg-white dark:bg-slate-800 shadow-lg ring-1 ring-black ring-opacity-5 dark:ring-slate-700 focus:outline-none z-30">
						<div className="py-1">{children}</div>
					</div>
				)}
			</div>
		);
	};

	const DropdownItem = ({
		children,
		href = "#",
	}: {
		children: React.ReactNode;
		href?: string;
	}) => (
		<a
			href={href}
			className="text-slate-700 dark:text-slate-200 block px-4 py-2 text-sm hover:bg-slate-100 dark:hover:bg-slate-700"
		>
			{children}
		</a>
	);

	const handleDropdownToggle = (title: string) => {
		setOpenDropdown((prev) => (prev === title ? null : title));
	};

	useEffect(() => {
		const handleClickOutside = (event: MouseEvent) => {
			if (navRef.current && !navRef.current.contains(event.target as Node)) {
				setOpenDropdown(null);
			}
		};
		document.addEventListener("mousedown", handleClickOutside);
		return () => {
			document.removeEventListener("mousedown", handleClickOutside);
		};
	}, []);

	return (
		<div className="w-full">
			<nav
				ref={navRef}
				className="relative z-20 bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm border-b border-slate-200 dark:border-slate-700 sticky top-0"
			>
				<div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
					<div className="flex justify-between items-center h-16">
						<div className="flex-shrink-0">
							<span className="text-xl font-bold text-slate-900 dark:text-slate-100">
								Solancn UI
							</span>
						</div>

						<div className="hidden lg:flex items-center space-x-8">
							<Dropdown
								title="Products"
								isOpen={openDropdown === "Products"}
								onToggle={() => handleDropdownToggle("Products")}
							>
								<DropdownItem>Analytics</DropdownItem>
								<DropdownItem>Engagement</DropdownItem>
								<DropdownItem>Automation</DropdownItem>
							</Dropdown>
							<Dropdown
								title="Solutions"
								isOpen={openDropdown === "Solutions"}
								onToggle={() => handleDropdownToggle("Solutions")}
							>
								<DropdownItem>For Startups</DropdownItem>
								<DropdownItem>For Enterprise</DropdownItem>
								<DropdownItem>For Developers</DropdownItem>
							</Dropdown>
							<Dropdown
								title="Resources"
								isOpen={openDropdown === "Resources"}
								onToggle={() => handleDropdownToggle("Resources")}
							>
								<DropdownItem>Blog</DropdownItem>
								<DropdownItem>Documentation</DropdownItem>
								<DropdownItem>Help Center</DropdownItem>
							</Dropdown>
							<a
								href="#"
								className="cursor-pointer text-sm font-medium text-slate-600 hover:text-slate-900 dark:text-slate-300 dark:hover:text-slate-100 transition-colors"
							>
								Price
							</a>
							<a
								href="#"
								className="cursor-pointer text-sm font-medium text-slate-600 hover:text-slate-900 dark:text-slate-300 dark:hover:text-slate-100 transition-colors"
							>
								Contact
							</a>
							<button className="h-9 px-4 py-2 bg-slate-100 text-slate-800 hover:bg-slate-200 dark:bg-slate-800 dark:text-slate-200 dark:hover:bg-slate-700 inline-flex items-center justify-center rounded-md text-sm font-medium transition-colors">
								Login
							</button>
						</div>

						<div className="lg:hidden">
							<button
								onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
								className="inline-flex items-center justify-center p-2 rounded-md text-slate-700 hover:text-slate-900 hover:bg-slate-100 dark:text-slate-300 dark:hover:text-slate-100 dark:hover:bg-slate-800 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-slate-500 dark:focus:ring-slate-400"
								aria-controls="mobile-menu"
								aria-expanded={mobileMenuOpen}
							>
								<span className="sr-only">Open main menu</span>
								{mobileMenuOpen ? (
									<X className="block h-6 w-6" />
								) : (
									<Menu className="block h-6 w-6" />
								)}
							</button>
						</div>
					</div>
				</div>

				{mobileMenuOpen && (
					<div
						className="lg:hidden bg-white dark:bg-slate-900 border-t border-slate-200 dark:border-slate-700"
						id="mobile-menu"
					>
						<div className="px-2 pt-2 pb-4 space-y-1 sm:px-3">
							<a
								href="#"
								className="block px-3 py-3 rounded-md text-base font-medium text-slate-700 hover:text-slate-900 hover:bg-slate-50 dark:text-slate-300 dark:hover:text-slate-100 dark:hover:bg-slate-800"
							>
								Products
							</a>
							<a
								href="#"
								className="block px-3 py-3 rounded-md text-base font-medium text-slate-700 hover:text-slate-900 hover:bg-slate-50 dark:text-slate-300 dark:hover:text-slate-100 dark:hover:bg-slate-800"
							>
								Solutions
							</a>
							<a
								href="#"
								className="block px-3 py-3 rounded-md text-base font-medium text-slate-700 hover:text-slate-900 hover:bg-slate-50 dark:text-slate-300 dark:hover:text-slate-100 dark:hover:bg-slate-800"
							>
								Resources
							</a>
							<a
								href="#"
								className="block px-3 py-3 rounded-md text-base font-medium text-slate-700 hover:text-slate-900 hover:bg-slate-50 dark:text-slate-300 dark:hover:text-slate-100 dark:hover:bg-slate-800"
							>
								Price
							</a>
							<a
								href="#"
								className="block px-3 py-3 rounded-md text-base font-medium text-slate-700 hover:text-slate-900 hover:bg-slate-50 dark:text-slate-300 dark:hover:text-slate-100 dark:hover:bg-slate-800"
							>
								Contact
							</a>
							<button className="mt-4 w-full h-10 px-4 py-2 bg-slate-900 text-slate-50 hover:bg-slate-900/90 dark:bg-slate-100 dark:text-slate-900 dark:hover:bg-slate-200 inline-flex items-center justify-center rounded-md text-sm font-medium transition-colors">
								Login
							</button>
						</div>
					</div>
				)}
