"use client";

import React, {
	type ReactNode,
	useEffect,
	useId,
	useRef,
	useState,
} from "react";

// Type definitions
interface SearchIconProps {
	size?: number;
}

interface ButtonProps {
	asChild?: boolean;
	className?: string;
	variant?: "default" | "ghost";
	size?: "default" | "sm" | "icon";
	children?: ReactNode;
	onClick?: () => void;
}

interface InputProps {
	className?: string;
	id?: string;
	placeholder?: string;
	type?: string;
	autoFocus?: boolean;
}

interface NavigationMenuProps {
	children: ReactNode;
	className?: string;
}

interface NavigationMenuListProps {
	children: ReactNode;
	className?: string;
}

interface NavigationMenuItemProps {
	children: ReactNode;
	className?: string;
	role?: string;
	"aria-hidden"?: boolean;
	key?: number;
}

interface NavigationMenuLinkProps {
	href: string;
	className?: string;
	children: ReactNode;
}

interface PopoverContextType {
	isOpen: boolean;
	setIsOpen: (value: boolean | ((prev: boolean) => boolean)) => void;
	popoverRef: React.RefObject<HTMLDivElement | null>;
}

interface PopoverProps {
	children: ReactNode;
}

interface PopoverTriggerProps {
	children: ReactNode;
}

interface PopoverContentProps {
	children: ReactNode;
	className?: string;
	align?: "start" | "center" | "end";
}

// Helper components to replicate the UI library's functionality

// --- Icon Components ---
const SearchIcon: React.FC<SearchIconProps> = ({ size = 16, ...props }) => (
	<svg
		xmlns="http://www.w3.org/2000/svg"
		width={size}
		height={size}
		viewBox="0 0 24 24"
		fill="none"
		stroke="currentColor"
		strokeWidth="2"
		strokeLinecap="round"
		strokeLinejoin="round"
		{...props}
	>
		<circle cx="11" cy="11" r="8" />
		<line x1="21" y1="21" x2="16.65" y2="16.65" />
	</svg>
);

// This component relies on the parent's `group` and `aria-expanded` attribute for animations.
const MenuIcon: React.FC = () => (
	<svg
		className="pointer-events-none"
		width={16}
		height={16}
		viewBox="0 0 24 24"
		fill="none"
		stroke="currentColor"
		strokeWidth="2"
		strokeLinecap="round"
		strokeLinejoin="round"
		xmlns="http://www.w3.org/2000/svg"
	>
		<path
			d="M4 12L20 12"
			className="origin-center -translate-y-[7px] transition-all duration-300 ease-[cubic-bezier(.5,.85,.25,1.1)] group-aria-expanded:translate-x-0 group-aria-expanded:translate-y-0 group-aria-expanded:rotate-[315deg]"
		/>
		<path
			d="M4 12H20"
			className="origin-center transition-all duration-300 ease-[cubic-bezier(.5,.85,.25,1.8)] group-aria-expanded:rotate-45"
		/>
		<path
			d="M4 12H20"
			className="origin-center translate-y-[7px] transition-all duration-300 ease-[cubic-bezier(.5,.85,.25,1.1)] group-aria-expanded:translate-y-0 group-aria-expanded:rotate-[135deg]"
		/>
	</svg>
);

// --- UI Components ---
const Logo: React.FC = () => (
	<div className="flex items-center justify-center gap-2">
		<svg
			xmlns="http://www.w3.org/2000/svg"
			fill="none"
			viewBox="0 0 200 200"
			width="28"
			height="28"
		>
			<g clipPath="url(#cs_clip_1_flower-1)">
				<mask
					id="cs_mask_1_flower-1"
					style={{ maskType: "alpha" }}
					width="200"
					height="186"
					x="0"
					y="7"
					maskUnits="userSpaceOnUse"
				>
					<path
						fill="#fff"
						d="M150.005 128.863c66.681 38.481-49.997 105.828-49.997 28.861 0 76.967-116.658 9.62-49.997-28.861-66.681 38.481-66.681-96.207 0-57.727-66.681-38.48 49.997-105.827 49.997-28.86 0-76.967 116.657-9.62 49.997 28.86 66.66-38.48 66.66 96.208 0 57.727z"
					></path>
				</mask>
				<g mask="url(#cs_mask_1_flower-1)">
					<path fill="#fff" d="M200 0H0v200h200V0z"></path>
					<path
						fill="url(#paint0_linear_748_4711)"
						d="M200 0H0v200h200V0z"
					></path>
					<g filter="url(#filter0_f_748_4711)">
						<path fill="#FF58E4" d="M130 0H69v113h61V0z"></path>
						<path
							fill="#0CE548"
							fillOpacity="0.35"
							d="M196 91H82v102h114V91z"
						></path>
						<path
							fill="#FFE500"
							fillOpacity="0.74"
							d="M113 80H28v120h85V80z"
						></path>
					</g>
				</g>
			</g>
			<defs>
				<filter
					id="filter0_f_748_4711"
					width="278"
					height="310"
					x="-27"
					y="-55"
					colorInterpolationFilters="sRGB"
					filterUnits="userSpaceOnUse"
				>
					<feFlood floodOpacity="0" result="BackgroundImageFix"></feFlood>
					<feBlend
						in="SourceGraphic"
						in2="BackgroundImageFix"
						result="shape"
					></feBlend>
					<feGaussianBlur
						result="effect1_foregroundBlur_748_4711"
						stdDeviation="27.5"
					></feGaussianBlur>
				</filter>
				<linearGradient
					id="paint0_linear_748_4711"
					x1="186.5"
					x2="37"
					y1="37"
					y2="186.5"
					gradientUnits="userSpaceOnUse"
				>
					<stop stopColor="#0E6FFF" stopOpacity="0.51"></stop>
					<stop offset="1" stopColor="#00F0FF" stopOpacity="0.59"></stop>
				</linearGradient>
				<clipPath id="cs_clip_1_flower-1">
					<path fill="#fff" d="M0 0H200V200H0z"></path>
				</clipPath>
			</defs>
			<g style={{ mixBlendMode: "overlay" }} mask="url(#cs_mask_1_flower-1)">
				<path
					fill="gray"
					stroke="transparent"
					d="M200 0H0v200h200V0z"
					filter="url(#cs_noise_1_flower-1)"
				></path>
			</g>
			<defs>
				<filter
					id="cs_noise_1_flower-1"
					width="100%"
					height="100%"
					x="0%"
					y="0%"
					filterUnits="objectBoundingBox"
				>
					<feTurbulence
						baseFrequency="0.6"
						numOctaves="5"
						result="out1"
						seed="4"
					></feTurbulence>
					<feComposite
						in="out1"
						in2="SourceGraphic"
						operator="in"
						result="out2"
					></feComposite>
					<feBlend
						in="SourceGraphic"
						in2="out2"
						mode="overlay"
						result="out3"
					></feBlend>
				</filter>
			</defs>
		</svg>
		<span className="font-bold text-lg tracking-wider">Solancn UI</span>
	</div>
);

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
	(
		{
			asChild = false,
			className = "",
			variant = "default",
			size = "default",
			children,
			...props
		},
		ref,
	) => {
		const Comp = asChild ? "span" : "button";
		const baseClasses =
			"inline-flex items-center justify-center rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50";
		const variantClasses: Record<string, string> = {
			default:
				"bg-slate-900 dark:bg-slate-100 text-white dark:text-slate-900 hover:bg-slate-900/90 dark:hover:bg-slate-200",
			ghost:
				"hover:bg-slate-100 dark:hover:bg-slate-800 hover:text-slate-900 dark:hover:text-slate-100",
		};
		const sizeClasses: Record<string, string> = {
			default: "h-10 px-4 py-2",
			sm: "h-9 rounded-md px-3",
			icon: "h-10 w-10",
		};
		const elementProps = props as React.HTMLAttributes<HTMLElement>;
		return (
			<Comp
				className={`${baseClasses} ${variantClasses[variant]} ${sizeClasses[size]} ${className}`}
				ref={ref}
				{...elementProps}
			>
				{children}
			</Comp>
		);
	},
);
Button.displayName = "Button";

const Input = React.forwardRef<HTMLInputElement, InputProps>(
	({ className = "", ...props }, ref) => (
		<input
			className={`flex h-10 w-full rounded-md border border-slate-200 dark:border-slate-700 bg-background dark:bg-slate-900 px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-slate-500 dark:placeholder:text-slate-400 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-slate-400 dark:focus-visible:ring-slate-600 focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 ${className}`}
			ref={ref}
			{...props}
		/>
	),
);
Input.displayName = "Input";

const NavigationMenu: React.FC<NavigationMenuProps> = ({
	children,
	className = "",
}) => <nav className={`relative z-10 ${className}`}>{children}</nav>;
const NavigationMenuList: React.FC<NavigationMenuListProps> = ({
	children,
	className = "",
}) => <ul className={`flex items-center ${className}`}>{children}</ul>;
const NavigationMenuItem: React.FC<NavigationMenuItemProps> = ({
	children,
	className = "",
	...props
}) => (
	<li className={`list-none ${className}`} {...props}>
		{children}
	</li>
);
const NavigationMenuLink: React.FC<NavigationMenuLinkProps> = ({
	href,
	className = "",
	children,
}) => (
	<a href={href} className={`block px-3 transition-colors ${className}`}>
		{children}
	</a>
);

// --- Popover Component ---
const PopoverContext = React.createContext<PopoverContextType | undefined>(
	undefined,
);

const Popover: React.FC<PopoverProps> = ({ children }) => {
	const [isOpen, setIsOpen] = useState(false);
	const popoverRef = useRef<HTMLDivElement>(null);
	useEffect(() => {
		const handleClickOutside = (event: MouseEvent) => {
			if (
				popoverRef.current &&
				!popoverRef.current.contains(event.target as Node)
			) {
				setIsOpen(false);
			}
		};
		document.addEventListener("mousedown", handleClickOutside);
		return () => document.removeEventListener("mousedown", handleClickOutside);
	}, []);
	return (
		<PopoverContext.Provider value={{ isOpen, setIsOpen, popoverRef }}>
			<div ref={popoverRef} className="relative">
				{children}
			</div>
		</PopoverContext.Provider>
	);
};

const PopoverTrigger: React.FC<PopoverTriggerProps> = ({ children }) => {
	const context = React.useContext(PopoverContext);
	if (!context) throw new Error("PopoverTrigger must be used within a Popover");
	const { isOpen, setIsOpen } = context;
	const child = React.Children.only(children);
	return React.cloneElement(
		child as React.ReactElement<{
			onClick?: () => void;
			"aria-expanded"?: boolean;
		}>,
		{
			onClick: () => setIsOpen((open: boolean) => !open),
			"aria-expanded": isOpen,
		},
	);
};

const PopoverContent: React.FC<PopoverContentProps> = ({
	children,
	className = "",
	align = "center",
}) => {
	const context = React.useContext(PopoverContext);
	if (!context) throw new Error("PopoverContent must be used within a Popover");
	const { isOpen } = context;
	const alignmentClasses: Record<string, string> = {
		start: "left-0",
		center: "left-1/2 -translate-x-1/2",
		end: "right-0",
	};
	if (!isOpen) return null;
	return (
		<div
			className={`absolute top-full mt-2 w-screen max-w-xs z-20 rounded-xl backdrop-blur-xl bg-white/80 dark:bg-black/90 border border-emerald-400/60 dark:border-emerald-500/70 p-2 ${alignmentClasses[align]} ${className}`}
		>
			{children}
		</div>
	);
};

// --- Main Header Component ---
const navigationLinks = [
	{ href: "#", label: "Products" },
	{ href: "#", label: "Categories" },
	{ href: "#", label: "Deals" },
];

function HeaderComponent() {
	const id = useId();
	const [isMobileSearchVisible, setIsMobileSearchVisible] = useState(false);

	return (
		<header className="border-b border-slate-200 dark:border-slate-700 bg-white dark:bg-black w-full">
			{/* Mobile Search View */}
			{isMobileSearchVisible && (
				<div className="flex h-16 items-center gap-2 lg:hidden px-4">
					<div className="relative flex-1">
						<Input
							id={id + "-mobile-search"}
							className="peer h-9 ps-9 pe-2 w-full"
							placeholder="Search..."
							type="search"
							autoFocus
						/>
						<div className="text-slate-400 pointer-events-none absolute inset-y-0 start-0 flex items-center justify-center ps-3">
							<SearchIcon size={16} />
						</div>
					</div>
					<Button
						variant="ghost"
						size="sm"
						onClick={() => setIsMobileSearchVisible(false)}
					>
						Cancel
					</Button>
				</div>
			)}

			{/* Default Header View */}
			<div
				className={`flex h-16 items-center justify-between gap-4 px-4 lg:px-6 ${isMobileSearchVisible ? "hidden" : "flex"}`}
			>
				{/* Left side: Mobile Menu, Logo, Desktop Nav */}
				<div className="flex items-center gap-4">
					<div className="lg:hidden">
						<Popover>
							<PopoverTrigger>
								<Button
									className="group size-8 hover:bg-emerald-400/20 dark:hover:bg-emerald-500/20 hover:border hover:border-emerald-400/40 dark:hover:border-emerald-500/40 transition-all duration-300 ease-out"
									variant="ghost"
									size="icon"
								>
									<MenuIcon />
								</Button>
							</PopoverTrigger>
							<PopoverContent align="start" className="w-48 p-1">
								<NavigationMenu className="max-w-none *:w-full">
									<NavigationMenuList className="flex-col items-start gap-0">
										{navigationLinks.map((link, index) => (
											<NavigationMenuItem key={index} className="w-full">
												<NavigationMenuLink
													href={link.href}
													className="py-2 px-3 text-black/90 dark:text-white/90 hover:text-black dark:hover:text-white hover:bg-emerald-400/20 dark:hover:bg-emerald-500/20 hover:border-l-2 hover:border-emerald-400/60 dark:hover:border-emerald-500/60 rounded-md transition-all duration-300 ease-out backdrop-blur-sm hover:shadow-sm hover:scale-[1.02]"
												>
													{link.label}
												</NavigationMenuLink>
											</NavigationMenuItem>
										))}
										<NavigationMenuItem
											className="w-full"
											role="presentation"
											aria-hidden={true}
										>
											<div
												role="separator"
												aria-orientation="horizontal"
												className="bg-black/20 dark:bg-white/20 -mx-1 my-1 h-px"
											></div>
										</NavigationMenuItem>
										<NavigationMenuItem className="w-full">
											<NavigationMenuLink
												href="#"
												className="py-2 px-3 text-black/90 dark:text-white/90 hover:text-black dark:hover:text-white hover:bg-emerald-400/20 dark:hover:bg-emerald-500/20 hover:border-l-2 hover:border-emerald-400/60 dark:hover:border-emerald-500/60 rounded-md transition-all duration-300 ease-out backdrop-blur-sm hover:shadow-sm hover:scale-[1.02]"
											>
												Sign In
											</NavigationMenuLink>
										</NavigationMenuItem>
									</NavigationMenuList>
								</NavigationMenu>
							</PopoverContent>
						</Popover>
					</div>
					<a
						href="#"
						className="text-black dark:text-white hover:text-gray-600 dark:hover:text-gray-300"
					>
						<Logo />
					</a>
					<NavigationMenu className="hidden lg:flex">
						<NavigationMenuList className="gap-2">
							{navigationLinks.map((link, index) => (
								<NavigationMenuItem key={index} className="">
									<NavigationMenuLink
										href={link.href}
										className="text-gray-600 dark:text-gray-300 hover:text-black dark:hover:text-white py-1.5 font-medium"
									>
										{link.label}
									</NavigationMenuLink>
								</NavigationMenuItem>
							))}
						</NavigationMenuList>
					</NavigationMenu>
				</div>

				{/* Right side: Search, Theme Toggle, and Sign In */}
				<div className="flex items-center gap-2">
					<div className="relative hidden lg:block">
						<Input
							id={id}
							className="peer h-9 ps-9 pe-2"
							placeholder="Search..."
							type="search"
						/>
						<div className="text-slate-400 dark:text-slate-500 pointer-events-none absolute inset-y-0 start-0 flex items-center justify-center ps-3 peer-disabled:opacity-50">
							<SearchIcon size={16} />
						</div>
					</div>
					<div className="lg:hidden">
						<Button
							variant="ghost"
							size="icon"
							className="size-8"
							onClick={() => setIsMobileSearchVisible(true)}
						>
							<SearchIcon size={18} />
						</Button>
					</div>

					<div className="hidden lg:flex">
						<Button
							asChild
							variant="ghost"
							size="sm"
							className="text-sm font-medium text-gray-600 dark:text-gray-300 hover:text-black dark:hover:text-white"
						>
							<a href="#">Sign In</a>
						</Button>
					</div>
				</div>
			</div>
		</header>
	);
}

export default HeaderComponent;

"use client";
import { motion, type Variants } from "framer-motion";
import { Bell, Home, Settings, User } from "lucide-react";
import type React from "react";

// --- MenuBar Component ---

interface MenuItem {
	icon: React.ReactNode;
	label: string;
	href: string;
	gradient: string;
	iconColor: string;
}

const menuItems: MenuItem[] = [
	{
		icon: <Home className="h-5 w-5" />,
		label: "Home",
		href: "#",
		gradient:
			"radial-gradient(circle, rgba(59,130,246,0.15) 0%, rgba(37,99,235,0.06) 50%, rgba(29,78,216,0) 100%)",
		iconColor: "group-hover:text-blue-500 dark:group-hover:text-blue-400",
	},
	{
		icon: <Bell className="h-5 w-5" />,
		label: "Notifications",
		href: "#",
		gradient:
			"radial-gradient(circle, rgba(249,115,22,0.15) 0%, rgba(234,88,12,0.06) 50%, rgba(194,65,12,0) 100%)",
		iconColor: "group-hover:text-orange-500 dark:group-hover:text-orange-400",
	},
	{
		icon: <Settings className="h-5 w-5" />,
		label: "Settings",
		href: "#",
		gradient:
			"radial-gradient(circle, rgba(34,197,94,0.15) 0%, rgba(22,163,74,0.06) 50%, rgba(21,128,61,0) 100%)",
		iconColor: "group-hover:text-green-500 dark:group-hover:text-green-400",
	},
	{
		icon: <User className="h-5 w-5" />,
		label: "Profile",
		href: "#",
		gradient:
			"radial-gradient(circle, rgba(239,68,68,0.15) 0%, rgba(220,38,38,0.06) 50%, rgba(185,28,28,0) 100%)",
		iconColor: "group-hover:text-red-500 dark:group-hover:text-red-400",
	},
];

// Animation variants for different parts of the menu
const itemVariants: Variants = {
	initial: { rotateX: 0, opacity: 1 },
	hover: { rotateX: -90, opacity: 0 },
};

const backVariants: Variants = {
	initial: { rotateX: 90, opacity: 0 },
	hover: { rotateX: 0, opacity: 1 },
};

const glowVariants: Variants = {
	initial: { opacity: 0, scale: 0.8 },
	hover: {
		opacity: 1,
		scale: 2,
		transition: {
			opacity: { duration: 0.5, ease: [0.4, 0, 0.2, 1] },
			scale: { duration: 0.5, type: "spring", stiffness: 300, damping: 25 },
		},
	},
};

const navGlowVariants: Variants = {
	initial: { opacity: 0 },
	hover: {
		opacity: 1,
		transition: {
			duration: 0.5,
			ease: [0.4, 0, 0.2, 1],
		},
	},
};

const sharedTransition = {
	type: "spring" as const,
	stiffness: 100,
	damping: 20,
	duration: 0.5,
};

function MenuBar(): React.JSX.Element {
	return (
		<motion.nav
			className="p-2 rounded-2xl bg-white/60 dark:bg-black/60 backdrop-blur-lg border border-gray-200/80 dark:border-gray-800/80 shadow-lg dark:shadow-gray-900/20 relative overflow-hidden"
			initial="initial"
			whileHover="hover"
		>
			<motion.div
				className="absolute -inset-2 rounded-3xl z-0 pointer-events-none"
				style={{
					background:
						"radial-gradient(circle, rgba(59,130,246,0.1) 0%, rgba(147,51,234,0.1) 50%, rgba(239,68,68,0.1) 100%)",
				}}
				variants={navGlowVariants}
			/>
			<ul className="flex items-center gap-2 relative z-10">
				{menuItems.map((item: MenuItem) => (
					<motion.li key={item.label} className="relative">
						<motion.div
							className="block rounded-xl overflow-visible group relative"
							style={{ perspective: "600px" }}
							whileHover="hover"
							initial="initial"
						>
							{/* Glow effect on hover */}
							<motion.div
								className="absolute inset-0 z-0 pointer-events-none rounded-2xl"
								variants={glowVariants}
								style={{
									background: item.gradient,
									opacity: 0,
								}}
							/>
							{/* Front-facing menu item */}
							<motion.a
								href={item.href}
								className="flex items-center gap-2 px-4 py-2 relative z-10 bg-transparent text-gray-600 dark:text-gray-300 group-hover:text-gray-900 dark:group-hover:text-white transition-colors rounded-xl"
								variants={itemVariants}
								transition={sharedTransition}
								style={{
									transformStyle: "preserve-3d",
									transformOrigin: "center bottom",
								}}
							>
								<span
									className={`transition-colors duration-300 ${item.iconColor}`}
								>
									{item.icon}
								</span>
								<span className="font-medium">{item.label}</span>
							</motion.a>
							{/* Back-facing menu item for the 3D flip effect */}
							<motion.a
								href={item.href}
								className="flex items-center gap-2 px-4 py-2 absolute inset-0 z-10 bg-transparent text-gray-600 dark:text-gray-300 group-hover:text-gray-900 dark:group-hover:text-white transition-colors rounded-xl"
								variants={backVariants}
								transition={sharedTransition}
								style={{
									transformStyle: "preserve-3d",
									transformOrigin: "center top",
									transform: "rotateX(90deg)",
								}}
							>
								<span
